import React, { useState } from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronDown } from "@fortawesome/free-solid-svg-icons";

const Header = () => {
  const [sidebar, setSidebar] = useState(false);

  window.addEventListener("scroll", function () {
    const header = document.querySelector(".header");
    header.classList.toggle("active", window.screenY > 200);
  });

  return (
    <>
      <header className="header">
        <div className="container flex">
          <div className="logo">
            <img src="goal.png" alt="hands shaking" />
            <br />
            <span id="tagLogo">trench</span>
          </div>
          <div className="nav">
            <ul
              className={sidebar ? "nav-links-sidebar" : "nav-links"}
              onClick={() => setSidebar(false)}
            >
              <li>
                <Link to="/">ABOUT US</Link>
              </li>
              <li>
                <Link to="/">THE APP </Link>
                <FontAwesomeIcon icon={faChevronDown} />
              </li>
              <li>
                <Link to="/culture">CULTURE </Link>
                <FontAwesomeIcon icon={faChevronDown} />
              </li>
              <li>
                <Link to="/media">MEDIA </Link>
                <FontAwesomeIcon icon={faChevronDown} />
              </li>
              <li>
                <Link to="/blog">BLOG </Link>
                <FontAwesomeIcon icon={faChevronDown} />
              </li>
              <li>
                <Link to="/contact">CONTACT US </Link>
                <FontAwesomeIcon icon={faChevronDown} />
              </li>
            </ul>
          </div>
          <button className="navbar-items-icon"></button>
        </div>
      </header>
    </>
  );
};

export default Header;
